// setup cấu hình api mặc định để sử dụng
import axios from "axios";

const axiosClient = axios.create({
    // Đoạn đầu của 1 API mặc định
    baseURL: "link mặc định", 
    // setup header có token bảo mật
    headers: {
        TokenSecurity: "add later",
    }


}); 

// sử dụng kĩ thuật interceptor
axiosClient.interceptors.response.use(
    (reponse) => {},
    // thất bại
    (error) => {
        Promise.reject(error.response.data.con)
    },
);

export default axiosClient;
